Entity:
class Entity:
	def __init__(self, name: str, chinese_name: str = None):
		self.name = name
		self.chinese_name = chinese_name

ProcessParameter:
class ProcessParameter(Entity):
	"""Base class for all process parameters."""
	def __init__(self, name: str, value: Union[str, float], unit: str = None, chinese_name: str = None, **kwargs):
		super().__init__(name, chinese_name)
		self.value = value
		self.unit = unit
		for key, value in kwargs.items():
			setattr(self, key, value)

SpindleSpeed:
class SpindleSpeed(ProcessParameter):
	"""Spindle rotation speed parameter."""
	def __init__(self, value: Union[str, float], unit: str = "rpm", **kwargs):
		super().__init__("spindle_speed", value, unit, chinese_name="主轴转速", **kwargs)

	def calculate_cutting_speed(self, diameter: float) -> float:
		"""Calculate cutting speed based on spindle speed and diameter."""
		if isinstance(self.value, (int, float)):
			return 3.14159 * diameter * self.value / 1000
		return None

