Event:
class Event:
	"""Base class for manufacturing events."""
	def __init__(self, name: str, chinese_name: str = None, **kwargs):
		self.name = name
		self.chinese_name = chinese_name
		for key, value in kwargs.items():
			setattr(self, key, value)

MachiningEvent:
class MachiningEvent(Event):
	"""Base class for machining events."""
	def __init__(self, name: str, process: MachiningProcess, chinese_name: str = None, **kwargs):
		super().__init__(name, chinese_name, **kwargs)
		self.process = process

ProcessStartEvent:
class ProcessStartEvent(MachiningEvent):
	"""Process start event."""
	def __init__(self, process: MachiningProcess, **kwargs):
		super().__init__(f"{process.name}_start", process, chinese_name="工序开始", **kwargs)

ProcessEndEvent:
class ProcessEndEvent(MachiningEvent):
	"""Process end event."""
	def __init__(self, process: MachiningProcess, **kwargs):
		super().__init__(f"{process.name}_end", process, chinese_name="工序结束", **kwargs)

ToolChangeEvent:
class ToolChangeEvent(Event):
	"""Tool change event."""
	def __init__(self, old_tool: CuttingTool, new_tool: CuttingTool, **kwargs):
		super().__init__("tool_change", chinese_name="换刀", **kwargs)
		self.old_tool = old_tool
		self.new_tool = new_tool

