Event:
class Event:
	def __init__(self, trigger: str, arg_names, *args):
		self.trigger = trigger
		self.arguments = {}
		for arg_name, arg_values in zip(arg_names, args):
			self.arguments[arg_name] = arg_values

ManufacturingEvent:
class ManufacturingEvent(Event):
	def __init__(self, trigger: str, arg_names, *args):
		arg_names = ['process', 'equipment', 'material', 'operator', 'time', 'location', 'parameters']
		super().__init__(trigger, arg_names, *args)

